INSERT INTO `books` (`bookid`, `author_id`, `introduction`, `name_english`, `name_translitteration`, `name_arabic`, `number_of_pages`, `version`, `status`) VALUES
(3, 1, '', 'Three Fundamental Principles', ' al-Usool at-Thalaathat ', 'الأصول الثلاثة', '97', '1', 1),
(2, 2, 'This is a translation of the Laamiyyah poem which is ascribed to Shaykh al-Islaam Ibn Taymiyyah (rahimahullaah). \nIt is only 16 verses of poetry, easy to memorize, and a great starting point in memorization for a child or a beginner in knowledge. \nThis qaseedah has a written manuscript in Jaami''ah Malik Saud (no. 6/1928), being written in the year 1353H), and it is found alongside two other works, al-Wasiyyah al-Kubraa, and al-Adillah al-Bayyinah, both of which are known works of Ibn Taymiyyah. \nIt has been mentioned by the Scholar of Iraq, Nu''maan al-Alusi (rahimahullaah), in his excellent book "Jalaa'' al-Aynayn Fee Muhaakamah al-Ahmadayn." Shaykh Abd al-Salaam bin Burjis (rahimahullaah) included this qaseedah as being amongst the poetry of Ibn Taymiyyah in his work, "al-Saheeh min al-Nadhm al-Faseeh Li Shaykh al-Islaam Ibn Taymiyyah" and a number of Scholars have explanations of this qaseedah, from the contemporaries Shaykh Saalih al-Suhaymee and also Shaykh Ahmad al-Mardawi (d. 1236H) almost two hundred years ago. \nIts ascription to Ibn Taymiyyah is not decisively established, however, the contents of the poetry are in agreement with the creed of Ahl al-Sunnah, and due its brevity and shortness of length, it is worth attention.\n\nPoem Contents\n\nThe poem comprises major elements of the belief of the Salaf and touches upon\n\nThe excellence of the Companions,\nMaking tawassul to Allaah through one''s love of them,\nThe superiority of Abu Bakr al-Siddeeq (radiallaahu anhu),\nReferring back to the Qur''an and the Sunnah,\nBelieving in the verses of the attributes upon the way of the Salaf,\nRefuting the Ash''aris who base their view on the Qur''an from the saying of a Trinitarian Christian,\nBelief in seeing Allaah (the Ru''yah) in the Hereafter,\nBelief in Allaah''s descent to the lowest heaven without how,\nBelief in the Meezaan, Hawd and Siraat (from the affairs of the Hereafter),\nThe punishment of the Fire and the reward of Paradise in relation to Divine Wisdom its connection to the topic of al-qadar,\nThe questioning in the grave,\nAnd establishing this was the creed of the four Imaams.', 'Laamiyah', '0', 'لامية', '16', '1', 1),
(1, 1, '', 'Al-Waajibaat Al-Mutahattimat', 'Al-Waajibaat Al-Mutahattimat', 'الْوَاجِبَاتُ الْمُتَحَتِّمَاتُ الْمَعْرِفَةُ عَلَى كُلِّ مُسْلِمٍ وَمُسْلِمَةٍ\r\n', '38', '1', 1),
(4, 1, '', 'Four Principles of Tawheed', 'Al-Qawaa’id al-Arba’ah', 'القواعد الأربع', '28', '1', 1),
(5, 1, '', 'The Nullifiers of Islaam ', 'Nawaaqid al-Islaam', 'نَوَاقِضُ الْإِسْلاَمِ', '11', NULL, 1),
(6, 0, '', '40 Hadeeth Nawawee', 'Al Arba''in An Nawawiyah', 'الأربعون النووية', NULL, NULL, 0),
